                      <TextField 
                        fullWidth
                        label=""
                        className={classes.textUser}
                        value={item.message}
                        margin="dense"
                        id="outlined-basic" 
                        multiline={true}
                        InputProps={{
                          readOnly: true
                        }}
                        variant="outlined" 
                      /> 
                    </div>
                  </Tooltip>
                  </ThemeProvider>
              }
