                </Tooltip>
            </InputAdornment>}
          />
        </ThemeProvider>
      </Paper>
    )
  }

  const viewChat = () => {
    return (
      <Popper 
        open={open} 
        anchorEl={anchorEl}
        placement={placement} 
        transition
      >
        {({ TransitionProps }) => (
          <Fade {...TransitionProps} timeout={300}>
            <Paper>
              <div className={classes.positionSizeViewChat} >
                {renderHeader()}
                {renderContentChat()}
                {renderInput()}
              </div>
            </Paper>
          </Fade>
        )}
      </Popper>
    )
  }

  const onClickDiv = () => {
    setOpen(true)
    setPlacement('top-end');
  }

  const renderAvatar = () => {
    return (
      <div className={classes.positionAvatar} onClick={() => onClickDiv()}>
