import React from "react";
import clsx from "clsx";
import { createStyles, Theme, makeStyles } from "@material-ui/core/styles";
import {
  ListItemIcon,
  Divider,
  ListItemText,
  ListItem,
  List,
  Grid,
  Button,
  Icon,
  IconButton,
  Typography,
  Card,
  CardContent,
  CardActions,
} from "@material-ui/core";
import { news } from "../Constant";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import WcIcon from "@material-ui/icons/Wc";
import PhoneIcon from "@material-ui/icons/Phone";
import LocationOnIcon from "@material-ui/icons/LocationOn";
import EmailIcon from "@material-ui/icons/Email";

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
    color: "#aaa",
    backgroundColor: theme.palette.background.paper,
  },

  posts: {
    marginTop: theme.spacing(2),
  },
  post: {
    marginBottom: theme.spacing(2),
  },
}));

interface InfoProps {
  className?: string;
  infoUser: User;
}

interface User {
  address: string;
  avatar: string;
  birthday: string;
  email: string;
  facebook: string;
  first_name: string;
  gender: string;
  google_id: string;
  id: string;
  intro: string;
  last_name: string;
  phone: string;
  mobile: string;
  skype: string;
  username: string;
  viber: string;
  website: string;
  zalo: string;
  created_at: string;
  updated_at: string;
}

const Info: React.FunctionComponent<InfoProps> = (props: InfoProps) => {
  const classes = useStyles();
  return (
    <div>
      <Typography variant="h6" style={{ padding: "0px 0px 8px 0px" }}>
        Tin tức nôi bật
      </Typography>
      <Grid container spacing={1}>
        {news.map((item: any, index: number) => {
          return (
            <Grid item xs={3} key={index}>
              {/* <img alt="nhadatmoi.net" src={item.image} style={{ width: "100%", height: "140px" }} /> */}
              <Typography>{item.title}</Typography>
              <Typography style={{ color: "#aaa" }}>{item.date}</Typography>
            </Grid>
          );
        })}
      </Grid>
      <Grid
        direction="row"
        alignItems="center"
        justify="center"
        container
        spacing={1}
        style={{ padding: "20px 0px 0px" }}
      >
        <Grid item xs={12}>
          <Button
            fullWidth
            style={{
              border: "2px solid #D9EDFE",
              borderRadius: "4px",
              margin: "0px 0px 8px 0px",
            }}
          >
            Xem tất cả
          </Button>
        </Grid>
      </Grid>
    </div>
  );
};

export default Info;
