import React, { Component } from 'react';
import { Widget, addResponseMessage, addLinkSnippet, addUserMessage } from 'react-chat-widget';
// import { Grid } from '@material-ui/core'
// import BaseContainer, { selector } from 'containers/BaseContainer'
// import { withStyles } from '@mate/rial-ui/core/styles'


class Chat extends Component {
  componentDidUpdate() {
    addResponseMessage("Nhà đất mới xin kính chào. Chúng tôi có thể giúp đỡ gì");
  }
 
  handleNewUserMessage = (newMessage: string) => {
    // Now send the message throught the backend API
    let addLink = {
      title: 'My awesome link',
      link: 'https://github.com/Wolox/react-chat-widget',
      target: '_blank'
    }
  }


  

  render() {
    return (
      <div className="App">
        <Widget
          handleNewUserMessage={this.handleNewUserMessage}
          // profileAvatar={logo}
          title="Nhà đất mới"
          subtitle="Chúng tôi sẵn sàng giúp đỡ"
          senderPlaceHolder="Nhập yêu cầu ... "
          showCloseButton={false}
          fullScreenMode={false}
          badge={1} // biểu hiện số tin nhắn
          autofocus={false} // tự động focus vào input
        />
      </div>
    );
  } 
}


 
export default Chat;
