import React, { FunctionComponent } from "react";
import CheckCircleIcon from "@material-ui/icons/CheckCircle";
import Paper from "@material-ui/core/Paper";
import _ from "lodash";
import { Tabs, Tab, Divider, Grid, Button, Typography, Avatar, Hidden } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import {
  introduceInfo,
  infoTitle,
  accessTitle,
  accessImage,
  contentAccessOne,
  contentAccessTwo,
  titleUseTechnology,
  technologyImage,
  contentUseTechnologyOne,
  contentUseTechnologyTwo,
  contentUseTechnologyThree,
  contentUseTechnologyFour,
  contentUseTechnologyFive,
  agencyTitle,
  agencyContentOne,
  agencyContentTwo,
  agencyContentThree,
  agencyContentFour,
  agencyContentFive,

  postsInfo,

} from "./Constants";
import { mainURL } from "../../constants";

const Mission = () => {
  
  const renderContent = (element: any) => {
    return (
      <Grid item md={6}>
      <Typography variant="h5" style={{ padding: "20px 0px", fontWeight: 700 }}>
        {element.title}
      </Typography>
      <Typography style={{}}>{contentAccessOne}</Typography>
      <Typography style={{}}>{contentAccessTwo}</Typography>
    </Grid>
    )
  }
  const renderImage = (element: any) => {
    return (
      <Grid item md={6}>
        <img src={element.image} />
      </Grid>
    )
  }
  return (
    <div className={"mission-area body-general"}>
      {postsInfo.map((element: any, index: number) => {
        return (
          <div>
            { element.type === 1 
              ? <Grid container direction="row" spacing={3} alignItems={"center"}>
                  {renderContent(element)}
                  {renderImage(element)}
                </Grid>
              : <Grid container direction="row" spacing={3} alignItems={"center"}>
                  {renderImage(element)}
                  {renderContent(element)}
                </Grid>
            }
          </div>
        )
      })}
    </div>
  );
};

export default Mission;

