import React, { FunctionComponent } from "react";
import _ from 'lodash'
import { Grid, Button, Typography, Hidden } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles"
import Banner from "./Banner";
import Introduce from "./Introduce";
import Benefit from "./Benefit"
import AboutUs from "./AboutUs";
import { introduceInfo, infoTitle, priceListTitle, priceListContent } from "./Constants"

const indexStyles = makeStyles((theme) => ({
  root: {
    padding: "40px 80px", 
    color: '#444',
    [theme.breakpoints.down("sm")]: {
      padding: "8px 12px", 
    },
  },
  introduceInfo: {
    padding: "12px 0px", 
    textAlign: "justify"
  },
}));

const Index: FunctionComponent = () => {
  const classes = indexStyles();
  return (
    <div>
      <Banner/>
      <div className={classes.root} >
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <div className={classes.introduceInfo} >
              {introduceInfo}
            </div>
          </Grid>
        </Grid>
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <div style={{ padding: "20px 200px", textAlign: "center" }}>
              <Typography variant="h5" style={{ fontWeight: 900, }} >
                {infoTitle}
              </Typography>
            </div>
          </Grid>
        </Grid>
        {/* <Introduce/> */}
        {/* <Benefit /> */}
        {/* <Typography variant="h4" style={{ fontWeight: 700, textAlign: "center" }}>
          {priceListTitle}
        </Typography>
        <Typography style={{ textAlign: "center", padding: "20px 20%", }}>
          {priceListContent}
        </Typography>
        <Typography style={{ textAlign: "center", padding: "20px", color: "#1A4684", cursor: 'default', fontWeight: 700 }}>
          Hiển thị chi tiết
        </Typography> */}
        {/* <AboutUs/> */}
      </div>
    </div>
  );
};

export default Index;
