import React from 'react';
import {NextPage} from "next";
import Layout from '../../components/Layout/Layout';
import {apolloClient} from "../../utils";
import {GET_AD_SELL_LEASE_COMMENTS} from "../../graphql/comments/queries";
import {GET_USER_BY_ID } from "../../graphql/user/queries/index"
import "../../assets/scss/style.scss";
import { mainURL } from "../../constants";
import User from "../../components/User/Index"
 
interface ProjectDetails {
  url: { query: { id: string }},
  listComment: { data: { adSellLeaseComments: { edges: ListCommentProps[]}} },
  userId: string
  infoUser: UserProps
}

interface QueryProp { 
  query: { id: string } 
}

interface UserProps {
  address: String
  avatar: String
  birthday: String
  email: String
  facebook: String
  first_name: String
  gender: String
  google_id: String
  intro: String
  id: String
  last_name: String
  phone: String
  mobile: String
  skype: String
  username: String
  viber: String
  website: String
  zalo: String
  created_at: String
  updated_at: String
}

interface ListCommentProps {
  node: {
    id: string,
    ad_sell_lease_id: string,
    content: string,
    user_id: string,
    media: string,
    user: {
      first_name: string,
      last_name: string,
      avatar: string,
    }
    like_number: string,
    created_at: string,
  },
}

// @ts-ignore
const UserPage: NextPage<ProjectDetails> = (props: ProjectDetails) => {
  let metaTitle = `Chuyên viên tư vấn ${props.infoUser.first_name } ${props.infoUser.last_name}`
  return (
    <Layout
      title={metaTitle}
      description={"metaDescription"}
      {...props}
      image={"https://vcdn-giaitri.vnecdn.net/2020/01/23/C-OiQ-UUIAIycqR-1-1579796735_680x0.jpg"}
      canonical={ mainURL + '/nguoi-dung/' + props.userId}
      path={'nguoi-dung'}
      dataCompare={{}}
    >
      <User 
        listComment= {props.listComment.data.adSellLeaseComments.edges}
        userId={props.userId}
        infoUser={props.infoUser}
      />
    </Layout>
  );
};

// @ts-ignore
UserPage.getInitialProps = async function(props: QueryProp) {
  let userId = props.query.id;
  let filterObject =  {
    id: userId,
  };
  let dataFilter = JSON.stringify(filterObject);
  let listComment = await apolloClient.query({query: GET_AD_SELL_LEASE_COMMENTS, variables: {
      "page": 1,
      // "limit": 15,
      "limit": 2,
      "filter": dataFilter
    }}).then(response => {
    return response;
  });

  let infoUser =  await apolloClient.query({query: GET_USER_BY_ID, variables: {
    "id": userId
    }}).then(response => { 
      return response.data.user
  })
  
  return {
    listComment: listComment,
    userId: userId,
    infoUser: infoUser
  };
};

export default UserPage;
