import gql from "graphql-tag";

const GET_CHANNEL_BY_SLUG = gql`
  query channel ($slug: String) {
    channel (slug: $slug) {
      id
      name
    }
  }
`;

export {
  GET_CHANNEL_BY_SLUG
}
