import React, { Component } from 'react';
import Popper, { PopperPlacementType } from '@material-ui/core/Popper';
import SpeedDialIcon from '@material-ui/lab/SpeedDialIcon';
import SpeedDial, { SpeedDialProps } from '@material-ui/lab/SpeedDial';
import SpeedDialAction from '@material-ui/lab/SpeedDialAction';
import {
  Grid,
  Button,
  Fade,
  Typography,
  Paper,
  Card, 
  CardHeader,
  CardContent,
  CardActionArea, 
  CardMedia,
  Avatar,
  TextField,
  Badge
} from '@material-ui/core';
import { Theme, makeStyles, withStyles, createStyles } from '@material-ui/core/styles';


const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      width: 500,
    },
    typography: {
      padding: theme.spacing(2),
    },
    textField: {
      position: "absolute",
      bottom: "0px"
    },
    exampleWrapper: {
      position: 'relative',
      marginTop: theme.spacing(3),
      height: 380,
    },
    radioGroup: {
      margin: theme.spacing(1, 0),
    },
    avatar: {
      backgroundColor: "blue",
    },
    media: {
      height: 100,
      backgroundColor: "blue"
    },
    hearder: {
      backgroundColor: "#1a75ff",
      color: "white"
    },
    speedDial: {
      position: 'absolute',
      '&.MuiSpeedDial-directionUp, &.MuiSpeedDial-directionLeft': {
        bottom: theme.spacing(2),
        right: theme.spacing(2),
      },
      '&.MuiSpeedDial-directionDown, &.MuiSpeedDial-directionRight': {
        top: theme.spacing(2),
        left: theme.spacing(2),
      },
    },
    
  }),
);

const StyledBadge = withStyles((theme: Theme) =>
  createStyles({
    badge: {
      backgroundColor: '#44b700',
      color: '#44b700',
      boxShadow: `0 0 0 2px ${theme.palette.background.paper}`,
      '&::after': {
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        borderRadius: '50%',
        animation: '$ripple 1.2s infinite ease-in-out',
        border: '1px solid currentColor',
        content: '""',
      },
    },
    '@keyframes ripple': {
      '0%': {
        transform: 'scale(.8)',
        opacity: 1,
      },
      '100%': {
        transform: 'scale(2.4)',
        opacity: 0,
      },
    },
  }),
)(Badge);

const SmallAvatar = withStyles((theme: Theme) =>
  createStyles({
    root: {
      width: 22,
      height: 22,
      border: `2px solid ${theme.palette.background.paper}`,
    },
  }),
)(Avatar);

// const useStyles = makeStyles((theme: Theme) =>
//   createStyles({
//     root: {
//       display: 'flex',
//       '& > *': {
//         margin: theme.spacing(1),
//       },
//     },
//   }),
// );

export default function PositionedPopper() {
  const [anchorEl, setAnchorEl] = React.useState<HTMLButtonElement | null>(null);
  const [open, setOpen] = React.useState(false);
  const [placement, setPlacement] = React.useState<PopperPlacementType>();
  const classes = useStyles();

  const handleClick = (newPlacement: PopperPlacementType) => (
    event: React.MouseEvent<HTMLButtonElement>,
  ) => {
    setAnchorEl(event.currentTarget);
    setOpen((prev) => placement !== newPlacement || !prev);
    setPlacement(newPlacement);
  };

  return (
    <div className={classes.root}>
      <Popper open={open} anchorEl={anchorEl} placement={placement} transition>
        {({ TransitionProps }) => (
          <Fade {...TransitionProps} timeout={350}>
            <Paper>
              <div style={{ height:"600px", width: "400px" }} >
                <CardActionArea> 
                  <CardHeader
                    className={classes.hearder}
                    // <Avatar alt="nhadatmoi.net" className={classes.avatar} src={avatarUser} />
                    avatar={
                      <Avatar 
                        src={"https://image.crisp.chat/avatar/operator/da8d4459-5ea3-4119-bca1-9170f49ee69c/240/?1585999578742&quot"} 
                        aria-label="recipe" className={classes.avatar} 
                      />
                    }
                    title="Nhà đất mới"
                    subheader="trực tuyến"
                  />
                </CardActionArea>
                <TextField 
                  fullWidth
                  className={classes.textField}
                  id="outlined-basic" 
                  label="Outlined" 
                  variant="outlined"
                  placeholder="Nhap tin nhan ..."
                />
              </div>
            </Paper>
          </Fade>
        )}
      </Popper>
      <Grid container justify="center">
        <Grid item>
          <div className={classes.exampleWrapper} style={{ position: 'fixed', right: "50px", bottom: "50px" }}>
            <SpeedDial
              ariaLabel="SpeedDial example"
              className={classes.speedDial}
              icon={<SpeedDialIcon onClick={handleClick('top-end')} />}
              open={true}
            />
          </div>
        </Grid>
        <Grid item>
          <div className={classes.root}>
            <StyledBadge
              overlap="circle"
              anchorOrigin={{
                vertical: 'bottom',
                horizontal: 'right',
              }}
              variant="dot"
            >
          <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
            </StyledBadge>
            <Badge
              overlap="circle"
              anchorOrigin={{
                vertical: 'bottom',
                horizontal: 'right',
              }}
              badgeContent={<SmallAvatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />}
            >
              <Avatar alt="Travis Howard" src="/static/images/avatar/2.jpg" />
            </Badge>
          </div>
        </Grid>
      </Grid>
    </div>
  );
}

