import React, { useEffect, useState, FunctionComponent } from 'react';
import { Button, Typography, Grid, Icon } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import ClickAwayListener from '@material-ui/core/ClickAwayListener';
import Grow from '@material-ui/core/Grow';
import Paper from '@material-ui/core/Paper';
import Popper from '@material-ui/core/Popper';
import MenuItem from '@material-ui/core/MenuItem';
import MenuList from '@material-ui/core/MenuList';
import Validator from "../../modules/validator";
import SearchIcon from '@material-ui/icons/Search';
import {localStorageSearch} from "../../constants";
import {apolloClient} from "../../utils";
import {GET_PROJECT_LIST_NAME} from "../../graphql/project/queries";
import {makeStyles, createStyles, Theme} from '@material-ui/core/styles';

const useStyles = makeStyles((theme) =>
  createStyles({
    root: {
      display: 'flex',
    },
    paper: {
      marginRight: theme.spacing(2),
    },
  }),
);

const projectHot = [
  { title: "Vinhomes Central Park" },
  { title: "Vinhomes Grand Park" },
  { title: "Vinhomes Smart City" },
  { title: "Nhà Đất Hải Phòng" },
  { title: "Nhà Đất Hà Nội" }
]

interface DataSearchServerQuery  {
  edges: {
    node: DataSearch
  }[],
}

interface DataSearch {
  name: string
}


let dataSearch = new Array()
const MenuListComposition: FunctionComponent = () => {
  const classes = useStyles();
  const [open, setOpen] = useState(false);
  const [valueSearch, setValueSearch] = useState("");
  const anchorRef = React.useRef(null);
  const [projectSearch, setProjectSearch] = useState<DataSearchServerQuery>();
  const handleToggle = () => {
    setOpen(prevOpen => !prevOpen);
  }

  function handleListKeyDown(event: any) {
    if (event.key === 'Tab') {
      event.preventDefault();
      setOpen(false);
    }
  }

  const removeLocalSearch = () => {
    localStorage.removeItem(localStorageSearch)
    setOpen(false)
    setValueSearch("")
  }

  const prevOpen = React.useRef(open)
  useEffect(() => {
    
    dataSearch = JSON.parse(localStorage.getItem(localStorageSearch) as string)
    if(!Array.isArray(dataSearch)) dataSearch = []
    if(!Validator.isBlank(localStorage.getItem(localStorageSearch))) {
      localStorage.setItem(localStorageSearch, JSON.stringify(dataSearch))
    } 
    if (prevOpen.current === true && open === false) {
      // anchorRef.current.focus();
    }
    prevOpen.current = open
  }, [open])

  const onBlur = (e: any) => {
    if(!Array.isArray(dataSearch)) dataSearch = []
    if(e.target.value){
      let checkExist = false
      dataSearch.map(item => {
        if(item && item.title === e.target.value) {
          checkExist = true
        }
      })
      
      if(!checkExist || dataSearch.length == 0){
        dataSearch.unshift({ title: e.target.value})
      }
      if(dataSearch.length >= 4){
        dataSearch.splice(3, 1)        
      }
      localStorage.setItem(localStorageSearch, JSON.stringify(dataSearch))
    }
  }
  
  const handleCloseSearch = (index: number) => {
    let titleSearch = projectHot[index].title || ""
    setValueSearch(titleSearch)
    setOpen(false);
  }

  const handleCloseHistory = (index: number) => {
    if(index !== -1){
      let titleSearch = dataSearch[index].title || ""
      setValueSearch(titleSearch)
    }
    setOpen(false)
  }

  const handleProjectSearch = (index: number) => {
    let name = projectSearch.edges[index].node.name
    setValueSearch(valueSearch)
  }

  const onChangeSearch = (e: any) => {
    setValueSearch(e.target.value)
    let filterObject = { name: e.target.value }
    let dataFilter = JSON.stringify(filterObject)
    apolloClient.query({query: GET_PROJECT_LIST_NAME, variables: {
      "page": 1,
      "limit": 5,
      "filter": dataFilter
    }}).then(response => { 
      setProjectSearch(response.data.projects.edges)
    })
  }
  console.log("projectSearch", projectSearch)
  return (
    <div className={classes.root}>
      <Grid container spacing={1} justify="flex-start" alignItems="center">
        <Grid item xs={4}>
          <TextField
            fullWidth={true}
            // id="outlined-basic" 
            variant="outlined"
            onChange={(e: any) => onChangeSearch(e)}
            onBlur={(e: any) => onBlur(e)}
            value={valueSearch}
            autoComplete="off"
            ref={anchorRef}
            margin="dense"
            onClick={handleToggle}
          />
        </Grid>
        <Grid item xs={6}>
          <Button><SearchIcon />Tìm kiếm</Button>
        </Grid>
        <Grid item xs={2}></Grid>
        <Grid item xs={12}>
          <Popper open={open} anchorEl={anchorRef.current} role={undefined} transition disablePortal>
              {({ TransitionProps, placement }) => (
                <Grow {...TransitionProps} style={{ transformOrigin: placement === 'bottom' ? 'center top' : 'center bottom' }} >
                  <Paper>
                    <ClickAwayListener onClickAway={() => setOpen(false)}>
                      <MenuList autoFocusItem={open} id="menu-list-grow" onKeyDown={handleListKeyDown}>
                        {Array.isArray(projectSearch) 
                          ? <>
                            {projectSearch.map((item: any, index: number) => 
                              <MenuItem key={index} onClick={() => handleProjectSearch(index)}>
                                {item.node.name}
                              </MenuItem>)
                              } 
                            </>
                          : <>
                              {<MenuItem>
                                <Typography style={{ fontSize: "Roboto, sans-serif" }}>
                                  TỪ KHÓA HOT
                                </Typography>
                                </MenuItem>
                              }
                              {projectHot.map((item: any, index: number) => 
                              <MenuItem key={index} onClick={() => handleCloseSearch(index)}>{item.title}</MenuItem>)}
                              <MenuItem>
                                <Typography style={{ fontSize: "Roboto, sans-serif", marginRight: "120px" }} >
                                  LỊCH SỬ TÌM KIẾM
                                </Typography>
                                <Typography style={{ color: "red" }}onClick={removeLocalSearch}>
                                  Xóa 
                                </Typography>
                              </MenuItem>
                              {dataSearch.map((item: any, index: number) =>
                                <MenuItem key={index} onClick={() => handleCloseHistory(index)}>{item.title} 
                                </MenuItem>)}
                            </>
                        
                        }
                      </MenuList>
                    </ClickAwayListener>
                  </Paper>
                </Grow>
              )}
            </Popper>
        </Grid>
      </Grid>
    </div>
  );
}

export default MenuListComposition
