import React from "react";
import _ from "lodash";
import { Grid, Button, Typography, Hidden } from "@material-ui/core";
import { contentAccessOne, contentAccessTwo, postsInfo } from "./Constants";
import { makeStyles } from "@material-ui/core/styles"

const indexStyles = makeStyles((theme) => ({
  paddingPostsInfo: {
    padding: "20px 0px",
    [theme.breakpoints.down("sm")]: {
      padding: "8px 8px"
    },
  },
}));
const Mission = () => {
  const classes = indexStyles();
  const renderContent = (element: any) => {
    return (
      <Grid item md={6}>
        <Typography variant="h5" style={{ padding: "20px 0px", fontWeight: 700 }}>
          {element.title}
        </Typography>
        {element.contents.map((content: any, count: number) => {
          return <Typography key={count}>{content.item}</Typography>
        })}
      </Grid>
    )
  };
  
  const renderImage = (element: any) => {
    return (
      <Grid item md={6}>
        <img src={element.image} />
      </Grid>
    )
  };

  return (
    <div className={"mission-area body-general"}>
      {postsInfo.map((element: any, index: number) => {
        return (
          <div className={classes.paddingPostsInfo}>
            { element.type === 1 
              ? <Grid container direction="row" spacing={3} alignItems={"center"}>
                  {renderContent(element)}
                  {renderImage(element)}
                </Grid>
              : <Grid container direction="row" spacing={3} alignItems={"center"}>
                  {renderImage(element)}
                  {renderContent(element)}
                </Grid>
            }
          </div>
        )
      })}
    </div>
  );
};

export default Mission
