import React from 'react';
import {NextPage} from "next";
import Layout from '../../components/Layout/Layout';
import {apolloClient} from "../../utils";
import {GET_CHANNEL_BY_SLUG } from "../../graphql/channel/queries";
import "../../assets/scss/style.scss";
import { mainURL } from "../../constants";
import Channel from "../../components/Channel/Index"
 
interface IndexProps {
  // url: { query: { id: string }},
  // comments: { data: { adSellLeaseComments: { edges: CommentsProp[]}} },
  // ratings: { data: { adSellLeaseRatings: { edges: RatingsProp[]}} },
  // infoUser: UserProp
}

interface QueryProp { 
  query: { slug: string } 
}

interface UserProp {
  address: string
  avatar: string
  birthday: string
  email: string
  facebook: string
  first_name: string
  gender: string
  google_id: string
  intro: string
  id: string
  last_name: string
  phone: string
  mobile: string
  skype: string
  username: string
  viber: string
  website: string
  zalo: string
  created_at: string
  updated_at: string
}

interface CommentsProp {
  node: {
    id: string,
    ad_sell_lease_id: string,
    content: string,
    user_id: string,
    media: string,
    user: {
      first_name: string,
      last_name: string,
      avatar: string,
    }
    like_number: string,
    created_at: string,
  },
}

interface RatingsProp {
  node: {
    id: string,
    ad_sell_lease_id: string,
    content: string,
    user_id: string,
    media: string,
    user: {
      first_name: string,
      last_name: string,
      avatar: string,
    }
    rate: number,
    like_number: string,
    created_at: string,
  },
}

// @ts-ignore
const UserPage: NextPage<IndexProps> = (props: IndexProps) => {
  let metaTitle = `Channel`
  let imgBanner = "https://static.nhadatmoi.net/project/1585911111698_chung-cu-b2-truong-sa-phuong-17-quan-binh-thanh-1.jpg"
  return (
    <Layout
      title={metaTitle}
      description={""}
      {...props}
      image={imgBanner}
      canonical={ mainURL + '/channel/'}
      path={'channel'}
      dataCompare={{}}
    >
      <Channel />
    </Layout>
  );
};

// @ts-ignore
UserPage.getInitialProps = async function(props: QueryProp) {
  let slug = props.query.slug;
  let channel = await apolloClient.query({ query: GET_CHANNEL_BY_SLUG, variables: {
      "slug": slug,
    }}).then(response => {
      console.log("response", response)
    // return response;
  });
  
  return {
    // channel: channel,
  };
};

export default UserPage;
