import gql from "graphql-tag"

const GET_INVESTOR_LIST = gql`
  query investors ($page: Int!, $limit: Int!, $filter: String!) {
    investors (page: $page, filter: $filter, limit: $limit) {
      edges {
        node {
          id
          name
        }
      }
    }
  }
`;

const GET_INVESTOR_BY_ID = gql`
  query investor ($id: String!) {
    investor (id: $id) {
      id
      name
      description
      slug
      cover
      address
      phone
      mobile
      email
      website
      image
    }
  }
`;

export {
  GET_INVESTOR_BY_ID,
  GET_INVESTOR_LIST
}
