import React, { useEffect, useState, FunctionComponent } from 'react';
import { 
  Button, Typography, Grid, Icon, Popover,
  ListItem, ListItemText, ListItemAvatar, ListItemIcon,
  Divider, Grow, Paper, Popper,
  MenuItem, MenuList, TextField,
  Hidden,
} from '@material-ui/core';
import ClickAwayListener from '@material-ui/core/ClickAwayListener';
import Validator from "../../modules/validator";
import SearchIcon from '@material-ui/icons/Search';
import {localStorageSearch} from "../../constants";
import { createMuiTheme, ThemeProvider } from '@material-ui/core/styles';
import {apolloClient} from "../../utils";
import {GET_PROJECT_LIST_NAME} from "../../graphql/project/queries";
import {makeStyles, createStyles, Theme} from '@material-ui/core/styles';
import EcoIcon from '@material-ui/icons/Eco';
import ViewList from '@material-ui/icons/ViewList';
import InputBase from "@material-ui/core/InputBase";
import Router from "next/router";

const useStyles = makeStyles((theme) =>
  createStyles({
    root: {
      display: 'flex',
    },
    widthPopper: {
      width: "100%"
    },
    paper: {
      marginRight: theme.spacing(2),
    },
    fontMenuItem: {
      fontWeight: 900,
      fontSize: "15px",
    },
    fontColorMenuItem: {
      position: "absolute", 
      right: "5px",
      color: "red",
      textAlign: "right",
      fontSize: "14px",
    },
  }),
)

const theme = createMuiTheme({
  overrides: {
    MuiMenuItem: { 
      root: {
        lineHeight: 1,
        fontSize: "14px",
      }
    }
  },
});

const projectHot = [
  { title: "Vinhomes Central Park" },
  { title: "Vinhomes Grand Park" },
  { title: "Vinhomes Smart City" },
  { title: "Nhà Đất Hải Phòng" },
  { title: "Nhà Đất Hà Nội" }
]

interface DataSearchServerQuery  {
  edges: {
    node: {
      name: string
    }
  }[],
}

interface Props {
  path: string
  marginLeft: string
  searchLocation: string
}

let dataSearch = new Array()
const MenuListComposition: FunctionComponent<Props> = (props: Props) => {
  const classes = useStyles();
  const [open, setOpen] = useState(false);
  const [keywordSearch, setKeywordSearch] = useState("");
  const anchorRef = React.useRef(null);
  const [projectSearch, setProjectSearch] = useState<DataSearchServerQuery>();
  const [anchorElSearchType, setAnchorElSearchType] = React.useState<HTMLButtonElement | null>(null);
  const dropdownSearch = [
    {type: 'du-an-bat-dong-san', name: 'Dự án'},
    {type: 'nha-dat-ban', name: 'Tin rao'},
  ];
  let defaultDropdown = dropdownSearch[0];
  if (props.path === 'tin-rao' || props.path === 'nha-dat-ban') {
    defaultDropdown = dropdownSearch[1];
  }
  const [typeSearch, setType] = React.useState(defaultDropdown);
  const handleToggle = () => {
    setOpen(prevOpen => !prevOpen);
  }

  function handleListKeyDown(event: any) {
    if (event.key === 'Tab') {
      event.preventDefault();
      setOpen(false);
    }
  }

  const removeLocalSearch = () => {
    localStorage.removeItem(localStorageSearch)
    setOpen(false)
    setKeywordSearch("")
  }
  const _id = open ? 'simple-popover' : undefined;
  const prevOpen = React.useRef(open)
  useEffect(() => {
    dataSearch = JSON.parse(localStorage.getItem(localStorageSearch) as string)
    if(!Array.isArray(dataSearch)) dataSearch = []
    if(!Validator.isBlank(localStorage.getItem(localStorageSearch))) {
      localStorage.setItem(localStorageSearch, JSON.stringify(dataSearch))
    } 
    if (prevOpen.current === true && open === false) {
      // anchorRef.current.focus();
    }
    prevOpen.current = open
  }, [open])

  const onBlur = (e: any) => {
    if(!Array.isArray(dataSearch)) dataSearch = []
    if(e.target.value){
      let checkExist = false
      dataSearch.map(item => {
        if(item && item.title === e.target.value) {
          checkExist = true
        }
      })
      
      if(!checkExist || dataSearch.length == 0){
        dataSearch.unshift({ title: e.target.value})
      }
      if(dataSearch.length >= 4){
        dataSearch.splice(3, 1)        
      }
      localStorage.setItem(localStorageSearch, JSON.stringify(dataSearch))
    }
  }
  
  const onClickSearch = (title: string, index: number) => {
    setKeywordSearch(title)
    setOpen(false)
  }

  const onChangeSearch = (e: any) => {
    if (e.key == "Enter" || e.keyCode == 13) {
      buttonSearch();
    }

    setKeywordSearch(e.target.value)
    let filterObject = { name: e.target.value }
    let dataFilter = JSON.stringify(filterObject)
    apolloClient.query({query: GET_PROJECT_LIST_NAME, variables: {
      "page": 1,
      "limit": 5,
      "filter": dataFilter
    }}).then(response => { 
      setProjectSearch(response.data.projects.edges)
    })
  }

  const buttonSearch = () => {
    // if (typeSearch.type === 'nha-dat-ban') {
    //   let urlProcess = { pathname: '/nha-dat-ban', query: { title: keywordSearch } }
    //   let slugAs = '/nha-dat-ban?title=' + keywordSearch;
    //   if (props.path === 'nha-dat-ban') {
    //     window.location.href = '/nha-dat-ban?title=' + keywordSearch;
    //     return true;
    //   } else {
    //     Router.replace(urlProcess, slugAs, { shallow: true });
    //   }
    // } else {
    //   let urlProcess = { pathname: '/du-an-bat-dong-san', query: { name: keywordSearch } }
    //   let slugAs = '/du-an-bat-dong-san?name=' + keywordSearch;
    //   if (props.path === 'du-an-bat-dong-san') {
    //     window.location.href = '/du-an-bat-dong-san?name=' + keywordSearch;
    //     return true;
    //   } else {
    //     Router.replace(urlProcess, slugAs, { shallow: true });
    //   }
    // }

    if (typeSearch.type === 'nha-dat-ban') {
      let urlProcess = { pathname: '/nha-dat-ban', query: { title: keywordSearch } }
      let slugAs = '/nha-dat-ban?title=' + keywordSearch;
      Router.push(urlProcess, slugAs, { shallow: false });
    } else {
      let urlProcess = { pathname: '/du-an-bat-dong-san', query: { name: keywordSearch } }
      let slugAs = '/du-an-bat-dong-san?name=' + keywordSearch;
      Router.push(urlProcess, slugAs, { shallow: false });
    }
  }

  const handleClickSearchType = (event: React.MouseEvent<HTMLButtonElement>) => {
    setAnchorElSearchType(event.currentTarget);
  };

  const handleChangeType = (inputType: {type: string, name: string}) => {
    setType(inputType);
    handleCloseSearchType();
  };

  const handleCloseSearchType = () => {
    setAnchorElSearchType(null);
  };

  const onChangeWards = (event: any) => {
    setKeywordSearch(event.target.value);
    if (event.key == "Enter" || event.keyCode == 13) {
      buttonSearch();
    }
  };
  const openSearchType = Boolean(anchorElSearchType);
  return (
    <div className={classes.root}>
      <ThemeProvider theme={theme}> 
        { props.searchLocation == 'header'
          ? <>
              <div className="header__center__search__icon">
                <svg viewBox="0 0 512 512">
                  <path d="M505 442.7L405.3 343c-4.5-4.5-10.6-7-17-7H372c27.6-35.3 44-79.7 44-128C416 93.1 322.9 0 208 0S0 93.1 0 208s93.1 208 208 208c48.3 0 92.7-16.4 128-44v16.3c0 6.4 2.5 12.5 7 17l99.7 99.7c9.4 9.4 24.6 9.4 33.9 0l28.3-28.3c9.4-9.4 9.4-24.6.1-34zM208 336c-70.7 0-128-57.2-128-128 0-70.7 57.2-128 128-128 70.7 0 128 57.2 128 128 0 70.7-57.2 128-128 128z"/>
                </svg>
              </div>
              <div className="header__center__search__box">
                <InputBase
                  fullWidth={true}
                  placeholder="Tìm kiếm ... "
                  defaultValue={keywordSearch}
                  value={keywordSearch}
                  autoComplete="off"
                  ref={anchorRef}
                  onChange={(e: any) => onChangeSearch(e)}
                  onBlur={(e: any) => onBlur(e)}
                  onClick={handleToggle}
                  onKeyUp={e => onChangeWards(e)} 
                />
              </div>
              <div className="header__center__search__type" >
                <Button 
                  onClick={handleClickSearchType} 
                  aria-describedby={_id} 
                >
                  <svg viewBox="0 0 320 512">
                    <path d="M143 352.3L7 216.3c-9.4-9.4-9.4-24.6 0-33.9l22.6-22.6c9.4-9.4 24.6-9.4 33.9 0l96.4 96.4 96.4-96.4c9.4-9.4 24.6-9.4 33.9 0l22.6 22.6c9.4 9.4 9.4 24.6 0 33.9l-136 136c-9.2 9.4-24.4 9.4-33.8 0z"/>
                  </svg>
                  {typeSearch.name}
                </Button>
                <Popover
                  id={_id}
                  open={openSearchType}
                  anchorEl={anchorElSearchType}
                  onClose={handleCloseSearchType}
                  anchorOrigin={{
                    vertical: 'bottom',
                    horizontal: 'center',
                  }}
                  transformOrigin={{
                    vertical: 'top',
                    horizontal: 'center',
                  }}
                  // className="header__center__search__type__popup"
                >
                  <Typography component={'span'}>
                    <ListItem button onClick={(event: React.MouseEvent<HTMLElement>) => handleChangeType(dropdownSearch[0])}>
                      <ListItemIcon>
                        <EcoIcon/>
                      </ListItemIcon>
                      <ListItemText primary={dropdownSearch[0].name}/>
                    </ListItem>
                    <ListItem button onClick={(event: React.MouseEvent<HTMLElement>) => handleChangeType(dropdownSearch[1])}>
                      <ListItemIcon>
                        <ViewList/>
                      </ListItemIcon>
                      <ListItemText primary={dropdownSearch[1].name}/>
                    </ListItem>
                  </Typography>
                </Popover>
              </div>
              <Button variant="outlined" onClick={buttonSearch} className="header__center__search__button">
                <svg viewBox="0 0 512 512">
                  <path d="M505 442.7L405.3 343c-4.5-4.5-10.6-7-17-7H372c27.6-35.3 44-79.7 44-128C416 93.1 322.9 0 208 0S0 93.1 0 208s93.1 208 208 208c48.3 0 92.7-16.4 128-44v16.3c0 6.4 2.5 12.5 7 17l99.7 99.7c9.4 9.4 24.6 9.4 33.9 0l28.3-28.3c9.4-9.4 9.4-24.6.1-34zM208 336c-70.7 0-128-57.2-128-128 0-70.7 57.2-128 128-128 70.7 0 128 57.2 128 128 0 70.7-57.2 128-128 128z"/>
                </svg>
              </Button>
            </>
          : <>
              <Hidden xsDown>
                <div className="search_homepage__body__box__select">
                  <Button onClick={handleClickSearchType} aria-describedby={_id} >
                    <svg viewBox="0 0 320 512">
                      <path d="M143 352.3L7 216.3c-9.4-9.4-9.4-24.6 0-33.9l22.6-22.6c9.4-9.4 24.6-9.4 33.9 0l96.4 96.4 96.4-96.4c9.4-9.4 24.6-9.4 33.9 0l22.6 22.6c9.4 9.4 9.4 24.6 0 33.9l-136 136c-9.2 9.4-24.4 9.4-33.8 0z"/>
                    </svg>
                    {typeSearch.name}
                  </Button>
                  <Popover
                    id={_id}
                    open={openSearchType}
                    anchorEl={anchorElSearchType}
                    onClose={handleCloseSearchType}
                    anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
                    transformOrigin={{ vertical: 'top', horizontal: 'center' }}
                    className="search_homepage__body__box__select__popup"
                  >
                    <Typography component={'span'}>
                      <ListItem button onClick={(event: React.MouseEvent<HTMLElement>) => handleChangeType(dropdownSearch[0])}>
                        <ListItemIcon>
                          <EcoIcon/>
                        </ListItemIcon>
                        <ListItemText primary={dropdownSearch[0].name}/>
                      </ListItem>
                      <ListItem button onClick={(event: React.MouseEvent<HTMLElement>) => handleChangeType(dropdownSearch[1])}>
                        <ListItemIcon>
                          <ViewList/>
                        </ListItemIcon>
                        <ListItemText primary={dropdownSearch[1].name}/>
                      </ListItem>
                    </Typography>
                  </Popover>
                  
                </div>
              </Hidden>
              <div className="search_homepage__body_box_input">
                <InputBase
                  fullWidth={true}
                  placeholder="Nhập thông tin tìm kiếm ... "
                  className="search_homepage__body_box_input_base"
                  defaultValue={keywordSearch}
                  // value={keywordSearch}
                  autoComplete="off"
                  ref={anchorRef}
                  onChange={(e: any) => onChangeSearch(e)}
                  onBlur={(e: any) => onBlur(e)}
                  onClick={handleToggle}
                  onKeyUp={e => onChangeWards(e)} 
                />
              </div>
              <Button className="search_homepage__body_box_button" variant="outlined" onClick={buttonSearch}>
                <SearchIcon/>
              </Button>
            </>
        }
        <Popper
          style={{ marginLeft: `${props.marginLeft}` }} 
          className={classes.widthPopper}
          placement="bottom-start"
          open={open} anchorEl={anchorRef.current} role={undefined} transition disablePortal
        >
          {({ TransitionProps, placement }) => (
            <Grow {...TransitionProps} style={{ transformOrigin: placement === 'bottom' ? 'center top' : 'center bottom' }} >
              <Paper >
                <ClickAwayListener onClickAway={() => setOpen(false)}>
                  {  keywordSearch && Array.isArray(projectSearch) ?
                    <MenuList id="menu-list-grow" onKeyDown={handleListKeyDown}>
                        {/* autoFocusItem={open}  */}
                      {projectSearch.map((item: any, index: number) => 
                        <MenuItem key={index} onClick={() => onClickSearch(item.node.name, index)}>
                          {item.node.name}
                        </MenuItem>)} 
                    </MenuList>
                    : <MenuList id="menu-list-grow" onKeyDown={handleListKeyDown}>
                    {/* autoFocusItem={open}  */}
                        {<MenuItem><Typography className={classes.fontMenuItem}>TỪ KHÓA HOT</Typography></MenuItem>}
                        {projectHot.map((item: any, index: number) => <MenuItem key={index} onClick={() => onClickSearch(item.title, index)}>{item.title}</MenuItem>)}
                        <MenuItem>
                          <Typography className={classes.fontMenuItem}>LỊCH SỬ TÌM KIẾM</Typography>
                          <Typography className={classes.fontColorMenuItem} onClick={removeLocalSearch}>Xóa</Typography>
                        </MenuItem>
                        {dataSearch.map((item: any, index: number) => <MenuItem key={index} onClick={() => onClickSearch(item.title, index)}>{item.title} </MenuItem>)}
                      </MenuList>
                  }
                </ClickAwayListener>
              </Paper>
            </Grow>
          )}
        </Popper>
      </ThemeProvider>
    </div>
  );
}

export default MenuListComposition
