import React, { Suspense } from "react";
import { Grid, Button, Typography } from "@material-ui/core";
import GridAdSellLeases from "../GridAdSellLeases";
import { apolloClient } from "../../../utils";
import { GET_AD_SELL_LEASE_CHANNEL_BY_ID } from "../../../graphql/channel/queries";
import { listAd, adStyles } from "../Constant";
import LoadingCircle from "../LoadingCircle"
interface ListAdProps {
  channelId: string
};

interface AdProps {
  node: {
    title: string;
    slug: string;
    created_at: string;
    updated_at: string;
    price: {
      unit: string;
      value: string;
    };
    avatar: {
      alt_text: string;
      src: string;
      description: string;
    };
    address: {
      text: string;
      ward: string;
      district: string;
      city: string;
    };
    contact: {
      image: string;
      name: string;
      address: string;
      mobile: string;
      phone: string;
      email: string;
    };
    area: {
      unit: string;
      value: string;
    };
  };
};

const Ad: React.FunctionComponent<any> = (props: ListAdProps) => {
  const classes = adStyles();
  const [list_ad, setListAd] = React.useState<AdProps[]>([]);
  const [page, setPage] = React.useState(1);

  const getAdChannel = async ()=> { 
    let id = props.channelId
    let list_ad = await apolloClient.query({ query: GET_AD_SELL_LEASE_CHANNEL_BY_ID, variables: {
      "id": id,
      "limit": 18,
      "page": 1,
    }})
    .then((response: any) => {
      return response.data.channelAdSellLeases.edges;
    });
    if(Array.isArray(list_ad)){
      setListAd(list_ad);
    };
  };

  React.useEffect(() => {
    getAdChannel();
  });

  const renderLoading = (show: boolean) => {
    if(show){
      return (
        <div style={{ height: "calc(100vh - 112px)", position: "relative" }}>
          <LoadingCircle />
        </div>
      )
    }
    return ""
  }

  const renderAdSellLease = () => {
    return (
      <Grid container spacing={0}>
        <Grid item xs={12}>
          <Suspense fallback={renderLoading(Array.isArray(listAd))}>
            {Array.isArray(listAd) ? 
              <GridAdSellLeases
                grid={{ xl: 3, lg: 4, md: 6, sm: 6, xs: 12 }}
                gridData={listAd}
                typeView={"grid"}
                className="grid__list"
              />
              : renderLoading(Array.isArray(listAd)) 
            }
          </Suspense>
        </Grid>
        <Grid item xs={12} className="pagination__block">
          {page !== 1 ? (
            <Button className="pagination__block__prev" onClick={() => setPage(page - 1)}>
              <svg viewBox="0 0 448 512">
                <path d="M152.485 396.284l19.626-19.626c4.753-4.753 4.675-12.484-.173-17.14L91.22 282H436c6.627 0 12-5.373 12-12v-28c0-6.627-5.373-12-12-12H91.22l80.717-77.518c4.849-4.656 4.927-12.387.173-17.14l-19.626-19.626c-4.686-4.686-12.284-4.686-16.971 0L3.716 247.515c-4.686 4.686-4.686 12.284 0 16.971l131.799 131.799c4.686 4.685 12.284 4.685 16.97-.001z" />
              </svg>
              Trang trước
            </Button>
          ) : (
            ""
          )}
          {listAd.length > 12 ? (
            <Button className="pagination__block__next" onClick={() => setPage(page + 1)}>
              Trang sau
              <svg viewBox="0 0 448 512">
                <path d="M295.515 115.716l-19.626 19.626c-4.753 4.753-4.675 12.484.173 17.14L356.78 230H12c-6.627 0-12 5.373-12 12v28c0 6.627 5.373 12 12 12h344.78l-80.717 77.518c-4.849 4.656-4.927 12.387-.173 17.14l19.626 19.626c4.686 4.686 12.284 4.686 16.971 0l131.799-131.799c4.686-4.686 4.686-12.284 0-16.971L312.485 115.716c-4.686-4.686-12.284-4.686-16.97 0z" />
              </svg>
            </Button>
          ) : (
            ""
          )}
        </Grid>
      </Grid>
    )
  };

  const renderAdSellLeaseChannel = () => {
    return (
      <Grid container spacing={0}>
        <Grid item xs={12}>
          <Suspense fallback={<h1>Loading data ....</h1>}>
            {Array.isArray(list_ad) ? 
              <GridAdSellLeases
                grid={{ xl: 3, lg: 3, md: 4, sm: 6, xs: 12 }} 
                gridData={list_ad}
                typeView={"grid"}
                className="grid__list"
              />
              : renderLoading()
            }
           </Suspense>
        </Grid>
        <Grid item xs={12} className="pagination__block">
          {page !== 1 
          ? <Button className="pagination__block__prev" onClick={() => setPage(page - 1)}>
              <svg viewBox="0 0 448 512">
                <path d="M152.485 396.284l19.626-19.626c4.753-4.753 4.675-12.484-.173-17.14L91.22 282H436c6.627 0 12-5.373 12-12v-28c0-6.627-5.373-12-12-12H91.22l80.717-77.518c4.849-4.656 4.927-12.387.173-17.14l-19.626-19.626c-4.686-4.686-12.284-4.686-16.971 0L3.716 247.515c-4.686 4.686-4.686 12.284 0 16.971l131.799 131.799c4.686 4.685 12.284 4.685 16.97-.001z" />
              </svg>
              Trang trước
            </Button>
          : "" }
          {list_ad.length > 12 
          ? <Button className="pagination__block__next" onClick={() => setPage(page + 1)}>
              Trang sau
              <svg viewBox="0 0 448 512">
                <path d="M295.515 115.716l-19.626 19.626c-4.753 4.753-4.675 12.484.173 17.14L356.78 230H12c-6.627 0-12 5.373-12 12v28c0 6.627 5.373 12 12 12h344.78l-80.717 77.518c-4.849 4.656-4.927 12.387-.173 17.14l19.626 19.626c4.686 4.686 12.284 4.686 16.971 0l131.799-131.799c4.686-4.686 4.686-12.284 0-16.971L312.485 115.716c-4.686-4.686-12.284-4.686-16.97 0z" />
              </svg>
            </Button>
          : "" } 
        </Grid>
      </Grid>
    );
  };

  return (
    <div className={classes.rootAd}>
      <Typography variant="h5" className={classes.titleAd}>Danh sách tin rao</Typography>
      {renderAdSellLeaseChannel()}
      {/* {renderAdSellLease()} */}
    </div>
  );
};

export default Ad;
